(function(){"use strict";if(!window.mdn){window.mdn={}}var modalDialog={showModal:function(modal){if(!window.mdn.multiAuthEnabled||!modal||window.matchMedia&&window.matchMedia("(max-width:47.9385em)").matches){return false}else{return true}},closeModal:function(modal,modalTrigger){modal.classList.add("hidden");modalTrigger.focus()},handleKeyboardEvents:function(modal,modalTrigger){var firstFocusable=document.querySelector("[data-first-focusable]");var lastFocusable=document.querySelector("[data-last-focusable]");modal.addEventListener("keydown",function(event){if(!event.shiftKey&&event.key==="Tab"&&event.target===lastFocusable){event.preventDefault();firstFocusable.focus()}if(event.shiftKey&&event.key==="Tab"&&event.target===firstFocusable){event.preventDefault();lastFocusable.focus()}});modal.addEventListener("keyup",function(event){if(event.key==="Escape"){modalDialog.closeModal(modal,modalTrigger)}})}};window.mdn.modalDialog=modalDialog})();(function(){"use strict";var authModalContainer=document.getElementById("auth-modal");if(!window.mdn.modalDialog.showModal(authModalContainer)){return}setTimeout(function(){var pageHeader=document.querySelector(".page-header");pageHeader.addEventListener("click",function(event){if(event.target.classList.contains("signin-link")){event.preventDefault();var modalContentContainer=authModalContainer.querySelector("section");var closeModalButton=document.getElementById("close-modal");authModalContainer.classList.remove("hidden");modalContentContainer.focus();closeModalButton.addEventListener("click",function(){window.mdn.modalDialog.closeModal(authModalContainer,event.target)});window.mdn.modalDialog.handleKeyboardEvents(authModalContainer,event.target)}})},100)})();
